package study;

public class variable_variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//10을 a 변수에 초기화 해보자.
		int a=10;
		//true 값을 b변수에 초기화 해보자.
		boolean b = true;
		//'a'라는 문자를 c변수에 초기화 해보자
		char c = 'a';
		//안녕하세요를 d변수에 초기화 해보자
		String d = "안녕하세요";
		//10000000000을 e변수에 초기화 해보자(long)
		long e =10000000000L;
		//우항은 int타입 java는 기본이 int임.
		//원주율 3.14159값을 f 변수에 초기화 해보자
		float f = 3.14f;
		System.out.println(a);
		String aa="1";
		System.out.println("1" + "2");
		System.out.println(true + "");
		System.out.println('A' + 'B');
		System.out.println('1' + 2);
		System.out.println('1' + '2');
		System.out.println('J' + "ava");
		//System.out.println(true + null);
		char cc=' ';
		System.out.println(7);			// print() 메소드는 줄바꿈을 하지 않음.
		System.out.println(3);			// 정수 출력
		System.out.println(3.14);		// 실수 출력
		System.out.println("자바!!");	// 문자열 출력
		System.out.println("문자열끼리의 " + "연결도 가능합니다.");
		System.out.println("숫자" + 3 + "과 문자열의 연결도 가능합니다.");

	}

}
